            />
            <button className="emoji-btn" title="–≠–º–æ–¥–∑–∏">
              <span className="emoji-icon">üòä</span>
            </button>
          </div>
          <button
            className="send-btn"
            onClick={sendMessage}
            disabled={!inputText.trim()}
            title="–û—Ç–ø—Ä–∞–≤–∏—Ç—å"
          >
            <span className="send-icon">üì§</span>
          </button>
        </div>
