import { useState,useEffect } from 'react';
import './Forms.css'
import { Navigate, NavLink, useNavigate } from 'react-router-dom';
function Registration(){

    
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [code, setCode] = useState('')
    const [isCode, setisCode] = useState(false)
    const [id, setId] = useState(-1)

    const navigate = useNavigate();

    const handleChangeName = (event) => {
        setName(event.target.value)
        
    }

    const handleChangeCode = (event) => {
        setCode(event.target.value)
    }

    const handleChangeEmail = (event) => {
        setEmail(event.target.value)
        
    }

    const handleChangePassword = (event) => {
        setPassword(event.target.value)
    } 


    const handleSendCode = async () => {
      try{
        const response = await fetch('http://localhost:8080/auth/send', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
              username:name,
              email:email,
              password:password
          }),
          // mode: 'cors'
        });

        if (response.ok) {
          const data = await response.text()
          setId(data)
          setisCode(true)
          
        } else {
          alert('Ошибка при отправке данных');
        }
      } catch (error) {
        console.error('Ошибка:', error);
      }
    }
    const handleSubmit = async (e) => {
      e.preventDefault();

      try {
        const response = await fetch(`http://localhost:8080/auth/signup/${id}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
              username:name,
              email:email,
              password:password,
              code:code
          }),
          // mode: 'cors'
        });

        if (response.ok) {
          navigate('/login')
        } else {
          alert('Ошибка при отправке данных');
        }
      } catch (error) {
        console.error('Ошибка:', error);
      }
    };


    return(
        <div className="forms_block">
            <form className="forms_register" onSubmit={handleSubmit}>
                <div className="main_form_text">Регистрация</div>
                <div className="form_block">
                    <label className="form_label">Имя</label>
                    <input className="form_input" value={name} onChange={handleChangeName}/>
                </div>
                <div className="form_block">
                    <label className="form_label">Пароль</label>
                    <input className="form_input" value={password} onChange={handleChangePassword}/>
                </div> 
                <div className="form_block">
                    <label className="form_label">Почта</label>
                    <div className='email_block'>
                      <input className="form_input_email" value={email} type="email" onChange={handleChangeEmail}/>
                      <button className="form_btn_email" type='button' onClick={handleSendCode}>код</button>
                    </div>
                    {isCode && (
                      <div className='form_block_code'>
                        <label className="form_label">Код</label>
                        <input className="form_input" value={code} onChange={handleChangeCode}/>
                      </div>
                    )}
                    
                </div>
                
                <button className="form_btn" type='submit'>Создать</button>
                <div className="little_form_text"><NavLink to = "/login">Войти</NavLink></div>
            </form>
        </div>
    )
}

export default Registration;
